package javaExamples;

public class string {
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String s="javagrt";
		String s1="";
		
		for(int i=0;i<s.length();i++) {
			s1=s.charAt(i)+s1;
		}
		System.out.println("reverse string:"+" "+s1);
	
	
	}

}
